INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48587, 18155, 'Eletriptan', 'Liver Diseases', 'Eletriptan is primarily metabolized by the liver.  In patients with mild to moderate hepatic impairment, increases of 18% in the peak plasma concentration (Cmax) and 34% in the systemic exposure (AUC) of eletriptan were observed.  Dosage adjustments should not be necessary in this patient population.  There are no data concerning the use of eletriptan in patients with severe hepatic impairment, thus it is not recommended.', '2', '"Product Information. Relpax (eletriptan)." Pfizer U.S. Pharmaceuticals  (2003):', 'DDInter', 0);
