INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48631, 25974, 'Eltrombopag', 'Thromboembolism', 'Thromboembolic complications may result from increases in platelet counts with TPO receptor agonists such as avatrombopag, lusutrombopag, and eltrombopag.  Consider the potential for increased risk of thromboembolism when administering these medications to patients with known risk factors for thromboembolism, including genetic prothrombotic conditions (Factor V Leiden, Prothrombin 20210 A, Antithrombin deficiency or Protein C or S deficiency).', '2', '"Product Information. Promacta (eltrombopag)." GlaxoSmithKline  (2008):|"Product Information. Doptelet (avatrombopag)." Dova Pharmaceuticals  (2018):|"Product Information. Mulpleta (lusutrombopag)." Shionogi USA Inc  (2018):', 'DDInter', 0);
