INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48855, 4460, 'Enfortumab vedotin', 'Hyperglycemia', 'Hyperglycemia occurred in patients treated with enfortumab vedotin, including death, and diabetic ketoacidosis (DKA) in those with and without preexisting-existing diabetes mellitus.  The incidence of Grade 3-4 hyperglycemia increased consistently in patients with higher body mass index and in patients with higher baseline A1C.  Closely monitor blood glucose levels in patients with, or at risk for, diabetes mellitus or hyperglycemia.  If blood glucose is elevated (>250 mg/dL), withhold treatment.', '2', '"Product Information. Padcev (enfortumab vedotin)." Seattle Genetics Inc  (2019):', 'DDInter', 0);
