INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48857, 4460, 'Enfortumab vedotin', 'Peripheral Nervous System Diseases', 'Peripheral neuropathy, predominantly sensory, occurred in 49% of the patients treated with enfortumab vedotin in clinical trials.  Peripheral neuropathy occurred in patients with or without preexisting peripheral neuropathy.  Monitor patients for symptoms of new or worsening peripheral neuropathy and consider dose interruption or dose reduction when peripheral neuropathy occurs.  Permanently discontinue treatment in patients that develop Grade >3 peripheral neuropathy.', '2', '"Product Information. Padcev (enfortumab vedotin)." Seattle Genetics Inc  (2019):', 'DDInter', 0);
