INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24501, 21498, 'Sapropterin', 'Kidney Diseases', 'Patients with renal impairment have not been evaluated in sapropterin clinical trials.  Monitor patients who have renal impairment carefully if receiving treatment with sapropterin.', '2', '"Product Information. Kuvan (sapropterin)." BioMarin Pharmaceutical Inc  (2007):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24502, 1892, 'Salmon calcitonin', 'Hypocalcemia', 'Calcitonin inhibits bone resorption, which results in decreased serum calcium levels.  Following single IV doses, a marked transient inhibition of the ongoing bone resorptive process occurs, while continuous use leads to a persistent, smaller decrease in rate of bone resorption.  Because of the drug''s acute hypocalcemic effects, patients administered calcitonin may, theoretically, experience tetany under certain circumstances.  Calcitonin should be administered cautiously to patients with preexisting hypocalcemia, since they may be particularly sensitive to these effects.  Hypocalcemia should preferably be corrected prior to initiation of therapy.  If calcitonin is used to increase bone mineral density, such as in osteoporosis, an adequate intake of calcium and vitamin D should be ensured throughout the course of treatment.', '2', '"Product Information. Calcimar (calcitonin)." Rhone Poulenc Rorer  (2001):|"Product Information. Miacalcin (calcitonin)." Novartis Pharmaceuticals  (2022):', 'DDInter', 0);
