INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24712, 281, 'Baricitinib', 'Peptic Ulcer Perforation', 'Baricitinib may cause gastrointestinal perforation.  Baricitinib-treated patients who may be at increased risk for gastrointestinal perforation (e.g., patients with history of diverticulitis) should be monitored.  Patients presenting with new onset abdominal symptoms should be evaluated promptly for early identification of gastrointestinal perforation.  Caution is recommended for patients who may be at increased risk.', '2', '"Product Information. Olumiant (baricitinib)." Lilly, Eli and Company  (2018):', 'DDInter', 0);
