INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24737, 281, 'Baricitinib', 'Hepatitis, Viral, Human', 'Viral reactivation, including cases of herpes virus reactivation (e.g., herpes zoster), was reported in clinical studies with baricitinib.  Patients should be screened for viral hepatitis in accordance with clinical guidelines before starting therapy with baricitinib.  If a patient develops herpes zoster during therapy, treatment with baricitinib should be interrupted until the episode resolves.', '2', '"Product Information. Olumiant (baricitinib)." Lilly, Eli and Company  (2018):', 'DDInter', 0);
