INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24761, 16835, 'Secukinumab', 'Tuberculosis', 'Before initiating certain interleukin inhibitors, patients should be screened for latent tuberculosis infection with a tuberculin skin test.  Do not administer these agents to patients with an active tuberculosis infection.  Patients testing positive in tuberculosis screening should be treated by standard medical practice prior to therapy.  Anti-tuberculosis therapy should be considered prior to initiation of therapy in patients with a past history of latent or active tuberculosis in whom an adequate course of treatment cannot be confirmed, and for patients with a negative test for latent tuberculosis but having risk factors for tuberculosis infection.', '2', '"Product Information. Stelara (ustekinumab)." Centocor Inc  (2009):|"Product Information. Ilaris (canakinumab)." Novartis Pharmaceuticals  (2009):|"Product Information. Cosentyx (secukinumab)." Novartis Pharmaceuticals  (2015):|"Product Information. Tremfya (guselkumab)." Janssen Biotech, Inc.  (2017):', 'DDInter', 0);
