INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24763, 16835, 'Secukinumab', 'Inflammatory Bowel Diseases', 'New onset and exacerbations of inflammatory bowel disease, in some cases serious, have been reported in patients treated with secukinumab.  Caution should be used when prescribing secukinumab to patients with inflammatory bowel disease.  Patients who are treated with secukinumab should be monitored for signs and symptoms of inflammatory bowel.', '2', '"Product Information. Cosentyx (secukinumab)." Novartis Pharmaceuticals  (2015):', 'DDInter', 0);
