INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53013, 0, 'Frangula purshiana bark', 'Inflammatory Bowel Diseases', 'The use of laxatives is contraindicated in patients with inflammatory bowel disease.  Patients with inflammatory bowel disease may experience colonic perforation with use of stimulant laxatives.', '3', '"Product Information. Dulcolax (bisacodyl)." Ciba Self-Medication Inc  (2001):|"Product Information. Fleet Bisacodyl (bisacodyl)." Fleet|"Product Information. Kondremul Plain (mineral oil)." Bristol-Myers Squibb|"Product Information. Neoloid (castor oil)." Paddock Laboratories Inc  (2001):|"Product Information. SenoSol-X (senna)." Apothecon Inc  (2022):|"Product Information. Suprep Bowel Prep Kit (magnesium/potassium/sodium sulfates)." Braintree Laboratories  (2010):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53014, 0, 'Frangula purshiana bark', 'Intestinal Obstruction', 'The use of laxatives is contraindicated in patients with intestinal obstruction disorders.  Patients with intestinal obstruction disorders may need their underlying condition treated to correct the constipation.  Some laxatives require reduction in the colon to their active form to be effective which may be a problem in patients with intestinal obstruction.', '3', '"Product Information. Dulcolax (bisacodyl)." Ciba Self-Medication Inc  (2001):|"Product Information. Fleet Bisacodyl (bisacodyl)." Fleet|"Product Information. Kondremul Plain (mineral oil)." Bristol-Myers Squibb|"Product Information. Fleet Mineral Oil Enema (mineral oil)." Fleet  (2001):|"Product Information. Citrucel (methylcellulose)." SmithKline Beecham  (2001):|"Product Information. Fleet Babylax (glycerin)." Alcon Laboratories Inc  (2001):|"Product Information. SenoSol-X (senna)." Apothecon Inc  (2022):|"Product Information. Suprep Bowel Prep Kit (magnesium/potassium/sodium sulfates)." Braintree Laboratories  (2010):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53015, 0, 'Frangula purshiana bark', 'Appendicitis', 'The use of stimulant laxatives is contraindicated in patients with or who may have acute surgical abdomen or appendicitis.  These patients may be candidates for emergency surgery.  Stimulant laxatives should also not be administered to patients with abdominal pain, particularly if the cause has not been determined.', '3', '"Product Information. Dulcolax (bisacodyl)." Ciba Self-Medication Inc  (2001):|"Product Information. Fleet Bisacodyl (bisacodyl)." Fleet|"Product Information. Kondremul Plain (mineral oil)." Bristol-Myers Squibb|"Product Information. SenoSol-X (senna)." Apothecon Inc  (2022):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53016, 0, 'Frangula purshiana bark', 'Rectal Diseases', 'The use of stimulant laxatives is contraindicated in patients with anal or rectal fissures.  These preparations may cause irritation, burning sensation, and proctitis.', '3', '"Product Information. Dulcolax (bisacodyl)." Ciba Self-Medication Inc  (2001):|"Product Information. Fleet Bisacodyl (bisacodyl)." Fleet', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53017, 19542, 'Gadobutrol', 'Nephrogenic Fibrosing Dermopathy', 'Gadolinium based contrast agents (GBCAs) increase the risk for nephrogenic systemic fibrosis (NSF) among patients with impaired elimination of the drugs.  Use of these agents should be avoided in these patients unless the diagnostic information is essential and unavailable with non- contrasted MRI or other diagnostic modalities.  NSF may result in fatal or debilitating fibrosis affecting the skin, muscle and internal organs.  The risk of NSF appears to be higher in patients with chronic severe renal disease and patients with acute kidney injury.  Patients should be screened for acute renal injury and other conditions that might affect renal function such as age >60 years old, hypertension, and diabetes.', '3', '"Product Information. Multihance (gadobenate dimeglumine)." Bracco Diagnostics Inc  (2005):|"Product Information. Eovist (gadoxetate)." Bayer Pharmaceutical Inc  (2008):|"Product Information. Gadavist (gadobutrol)." Bayer Pharmaceutical Inc  (2011):|"Product Information. Dotarem (gadoterate meglumine)." Guerbet LLC  (2017):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53018, 19542, 'Gadobutrol', 'Kidney Diseases', 'Gadolinium-based contrast agents (GBCAs) increase the risk for nephrogenic systemic fibrosis (NSF) among patients with impaired elimination of the drugs.  Gadobenate ion is eliminated predominately via the kidneys.  The risk for NSF appears highest among patients with chronic, severe kidney disease (GFR <30 mL/min/1.73m2), or acute kidney injury.  It is recommended to avoid the use of GBCAs in these patients unless the diagnostic information is essential and not available with non-contrasted MRI or other processes.  NSF may result in fatal or debilitating systemic fibrosis affecting the skin, muscle and internal organs.  Consider screening patients for acute kidney injury and other conditions that may reduce renal function and for patients at risk for chronically reduced renal function (e.g., age > 60 years, hypertension or diabetes), estimate the glomerular filtration rate (GFR) through laboratory testing.  For patients receiving hemodialysis, physicians may consider the prompt initiation of hemodialysis following the administration of a GBCA to enhance the contrast agent''s elimination.  The usefulness of hemodialysis in the prevention of NSF is unknown.', '3', '"Product Information. Multihance (gadobenate dimeglumine)." Bracco Diagnostics Inc  (2005):|"Product Information. Eovist (gadoxetate)." Bayer Pharmaceutical Inc  (2008):|"Product Information. Gadavist (gadobutrol)." Bayer Pharmaceutical Inc  (2011):|"Product Information. Dotarem (gadoterate meglumine)." Guerbet LLC  (2017):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53019, 19542, 'Gadobutrol', 'Asthma', 'Patients with history of bronchial asthma might have an increased risk for a hypersensitivity reaction to gadolinium-based contrast agents.  Patients should always be assessed for any history of a reaction to contrast media, asthma or other allergic disorders before the administration of these agents.', '2', '"Product Information. Gadavist (gadobutrol)." Bayer Pharmaceutical Inc  (2011):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53020, 19542, 'Gadobutrol', 'Vision Disorders', 'The use of certain Gadolinium-based contrast agents (GBCAs) in patients with severe renal or hepatic impairment may impair imaging performance.  If GBCAs are used in these patients, complete MRI no later than 60 minutes after GBCAs administration and use a paired non-contrast and contrast MRI set for diagnosis.  It is recommended to exercise caution when interpreting contrast MR images in the absence of companion non-contrast MR images', '2', '"Product Information. Multihance (gadobenate dimeglumine)." Bracco Diagnostics Inc  (2005):|"Product Information. Eovist (gadoxetate)." Bayer Pharmaceutical Inc  (2008):|"Product Information. Gadavist (gadobutrol)." Bayer Pharmaceutical Inc  (2011):|"Product Information. Dotarem (gadoterate meglumine)." Guerbet LLC  (2017):', 'DDInter', 0);
