INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53634, 24095, 'Gadodiamide', 'Hypersensitivity', 'Severe and fatal hypersensitivity reactions including anaphylaxis have been observed with administration of gadolinium based contrast agents, including gadoteridol.  Patients with a history of allergy, drug reactions or other hypersensitivity-like disorders should be closely observed during the procedure and for several hours after drug administration.  If a reaction occurs, stop administering and immediately begin appropriate therapy including resuscitation.', '2', '"Product Information. ProHance (gadoteridol)." Bracco Diagnostics Inc  (2001):', 'DDInter', 0);
