INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53780, 6611, 'Gefitinib', 'Gastrointestinal Diseases', 'The use of some EGFR inhibitors may be associated with gastrointestinal perforation and fatal cases have been reported.  Patients receiving concomitant anti-angiogenic agents, corticosteroids, NSAIDs, or taxane-based chemotherapy, or who have prior history of peptic ulceration or diverticular disease may be at increased risk of perforation.  Permanently discontinue these agents in patients who develop gastrointestinal perforation.', '3', '"Product Information. Iressa (gefitinib)." Astra-Zeneca Pharmaceuticals  (2003):|"Product Information. Tarceva (erlotinib)." Genentech  (2004):', 'DDInter', 0);
