INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53786, 6611, 'Gefitinib', 'Liver Diseases', 'Gefitinib undergoes extensive hepatic metabolism.  Obtain periodic liver function testing and withhold gefitinib for up to 14 days in patients with worsening liver function (Grade 2 or higher for ALT and/or AST elevations) and resume treatment when the adverse reaction fully resolves or improves to NCI CTCAE Grade 1.  Permanently discontinue therapy in patients with severe hepatic impairment.  Monitor adverse reactions when gefitinib is administered to patients with moderate and severe hepatic impairment.', '3', '"Product Information. Iressa (gefitinib)." Astra-Zeneca Pharmaceuticals  (2003):', 'DDInter', 0);
