INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53825, 3236, 'Ganciclovir', 'Kidney Diseases', 'Ganciclovir is primarily eliminated by the kidney.  Patients with renal impairment may be at greater risk for toxicity from ganciclovir due to decreased drug clearance.  Therapy with ganciclovir should be administered cautiously in these patients at reduced dosages.  Serum creatinine values should be monitored frequently, and the dosing further adjusted or discontinued accordingly.', '3', 'Sommadossi JP, Bevan R, Ling T, et al. "Clinical pharmacokinetics of ganciclovir in patients with normal and impaired renal function." Rev Infect Dis 10 (1988):  s507-14|Lake KD, Fletcher CV, Love KR, et al. "Ganciclovir pharmacokinetics during renal impairment." Antimicrob Agents Chemother 32 (1988):  1899-900|"Product Information. Cytovene (ganciclovir)." Genentech  (2002):', 'DDInter', 0);
