INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26565, 15778, 'Sulfinpyrazone', 'Hematologic Diseases', 'The use of sulfinpyrazone is contraindicated in patients with a current or past history of blood dyscrasias.  Anemia, leukopenia, agranulocytosis, thrombocytopenia, and aplastic anemia have been reported rarely with sulfinpyrazone use.  Phenylbutazone, a structurally related agent, has been associated frequently with bone marrow suppression, as well as an increased risk of leukemia.  Periodic blood counts are recommended when sulfinpyrazone or other pyrazole compounds are used.', '3', '"Multum Information Services, Inc. Expert Review Panel"|American Medical Association, Division of Drugs and Toxicology "Drug evaluations annual 1994." Chicago, IL: American Medical Association;  (1994):|"Product Information. Anturane (sulfinpyrazone)." Novartis Pharmaceuticals  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26566, 15779, 'Sulfinpyrazone', 'Hematologic Diseases', 'The use of sulfinpyrazone is contraindicated in patients with a current or past history of blood dyscrasias.  Anemia, leukopenia, agranulocytosis, thrombocytopenia, and aplastic anemia have been reported rarely with sulfinpyrazone use.  Phenylbutazone, a structurally related agent, has been associated frequently with bone marrow suppression, as well as an increased risk of leukemia.  Periodic blood counts are recommended when sulfinpyrazone or other pyrazole compounds are used.', '3', '"Multum Information Services, Inc. Expert Review Panel"|American Medical Association, Division of Drugs and Toxicology "Drug evaluations annual 1994." Chicago, IL: American Medical Association;  (1994):|"Product Information. Anturane (sulfinpyrazone)." Novartis Pharmaceuticals  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26567, 15778, 'Sulfinpyrazone', 'Gastrointestinal Diseases', 'The use of sulfinpyrazone is contraindicated in patients with active peptic ulcer disease or symptoms of gastrointestinal inflammation or ulceration.  Sulfinpyrazone may commonly cause upper gastrointestinal irritation.  It may also aggravate or reactivate peptic ulcer.  Therapy with sulfinpyrazone may be administered cautiously in patients with a history of healed peptic ulcer.  Taking the medication with food, milk, or antacids may lessen GI adverse effects.', '3', '"Product Information. Anturane (sulfinpyrazone)." Novartis Pharmaceuticals  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26568, 15779, 'Sulfinpyrazone', 'Gastrointestinal Diseases', 'The use of sulfinpyrazone is contraindicated in patients with active peptic ulcer disease or symptoms of gastrointestinal inflammation or ulceration.  Sulfinpyrazone may commonly cause upper gastrointestinal irritation.  It may also aggravate or reactivate peptic ulcer.  Therapy with sulfinpyrazone may be administered cautiously in patients with a history of healed peptic ulcer.  Taking the medication with food, milk, or antacids may lessen GI adverse effects.', '3', '"Product Information. Anturane (sulfinpyrazone)." Novartis Pharmaceuticals  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26569, 15778, 'Sulfinpyrazone', 'Dehydration', 'Adequate hydration is necessary during therapy with sulfinpyrazone to prevent the renal precipitation of urates.  Patients who are dehydrated (e.g., due to severe diarrhea or vomiting) may be at increased risk and should be encouraged to consume additional amounts of liquid or given intravenous fluid.  In general, fluid intake sufficient to yield a daily urinary output of at least 2 liters is recommended.  Maintenance of a slightly alkaline or neutral urine is also desirable.', '2', '"Product Information. Anturane (sulfinpyrazone)." Novartis Pharmaceuticals  (2001):', 'DDInter', 0);
