INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26571, 15778, 'Sulfinpyrazone', 'Kidney Diseases', 'Renal failure has occasionally been reported with the use of sulfinpyrazone.  Therapy with sulfinpyrazone should be administered cautiously in patients with significant renal impairment.  Adequate hydration and periodic monitoring of renal function are recommended.', '2', '"Product Information. Anturane (sulfinpyrazone)." Novartis Pharmaceuticals  (2001):', 'DDInter', 0);
