INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26793, 11196, 'Tacrolimus', 'Long QT Syndrome', 'Tacrolimus may prolong the QT/QTc interval and may cause Torsade de Pointes.  It is recommended to avoid tacrolimus in patients with congenital long QT syndrome.  In patients with congestive heart failure, bradyarrhythmias, those taking certain antiarrhythmic medications or other medicinal products that lead to QT prolongation, and those with electrolyte disturbances such as hypokalemia, hypocalcemia, or hypomagnesemia, consider monitoring of tacrolimus whole blood concentrations and obtaining electrocardiograms and monitoring electrolytes (magnesium, potassium, calcium) periodically during treatment.', '3', '"Product Information. Prograf (tacrolimus)." Fujisawa  (2001):', 'DDInter', 0);
