INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26951, 4949, 'Sunitinib', 'Long QT Syndrome', 'Sunitinib can cause QT prolongation in a dose-dependent manner, which may lead to an increased risk for ventricular arrhythmias, including Torsade de Pointes.  This agent should be administered with caution in patients with congenital long QTc syndrome, congestive heart failure, electrolyte abnormalities, or those who are taking medications known to prolong the QTc interval.  Serum electrolytes should be corrected before initiating therapy with this agent.  Periodic monitoring of ECGs and electrolytes is recommended in these patients.', '2', '"Product Information. Sutent (sunitinib)." Pfizer U.S. Pharmaceuticals Group  (2006):', 'DDInter', 0);
