INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (54001, 0, 'Gemtuzumab ozogamicin', 'Pancytopenia', 'Gemtuzumab ozogamicin is a myelosuppressive agent.  Assess blood counts prior to each dose and monitor blood counts frequently after treatment until resolution of cytopenias.  Monitor patients for signs and symptoms of bleeding during treatment with gemtuzumab ozogamicin .  Manage severe bleeding, hemorrhage or persistent thrombocytopenia using dose delay or permanent discontinuation of treatment, and provide supportive care per standard practice.', '2', '"Product Information. Mylotarg (gemtuzumab ozogamicin)." Wyeth-Ayerst Laboratories', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (54002, 0, 'Gemtuzumab ozogamicin', 'Long QT Syndrome', 'QT interval prolongation has been observed in patients treated with drugs containing calicheamicin.  When administering gemtuzumab ozogamicin to patients who have a history of or predisposition for QTc prolongation, who are taking medicinal products that are known to prolong QT interval, and in patients with electrolyte disturbances, obtain electrocardiograms (ECGs) and electrolytes prior to the start of treatment and as needed during administration.', '2', '"Product Information. Mylotarg (gemtuzumab ozogamicin)." Wyeth-Ayerst Laboratories', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (54003, 0, 'Gemtuzumab ozogamicin', 'Kidney Diseases', 'The pharmacokinetics of gemtuzumab ozogamicin in patients with severe renal impairment (CrCL 15-29 mL/min) is unknown.  Caution is advised if used on these patients.', '2', '"Product Information. Mylotarg (gemtuzumab ozogamicin)." Wyeth-Ayerst Laboratories', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (54004, 24254, 'Ganirelix', 'Hypersensitivity', 'Treatment with ganirelix is not advised in women with severe allergic conditions.', '3', '"Product Information. Antagon (ganirelix)." Organon  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (54005, 24254, 'Ganirelix', 'Liver Diseases', 'The pharmacokinetics of ganirelix acetate injection have not been determined in special populations such as renally impaired and hepatically impaired patients.  Caution is advised.', '2', '"Product Information. Antagon (ganirelix)." Organon  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (54006, 2572, 'Gentamicin', 'Dehydration', 'Adequate hydration is crucial to minimize the risk of oto- and nephrotoxicity associated with the use of aminoglycosides.  Dehydration should preferably be corrected prior to initiation of therapy.  In patients who may be at risk for dehydration, such as those with severe and/or prolonged diarrhea or vomiting, fluid status should be monitored closely.  If signs of renal irritation develop during therapy, hydration should be increased as indicated, accompanied by a reduction in dosage if necessary.  Therapy should be withdrawn if urinary output decreases progressively or azotemia increases.', '3', '"Product Information. Amikin (amikacin)." Bristol-Myers Squibb  (2002):|"Product Information. Garamycin (gentamicin)." Schering-Plough Corporation  (2007):|"Product Information. Netromycin (netilmicin)." Schering Corporation  (2002):|"Product Information. Streptomycin Sulfate (streptomycin)." Pfizer U.S. Pharmaceuticals|"Product Information. Nebcin (tobramycin)." Lilly, Eli and Company  (2001):|"Product Information. Mycifradin (neomycin)." Emerson Laboratories  (2001):', 'DDInter', 0);
