INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (54230, 2572, 'Gentamicin (topical)', 'Burns', 'Topical aminoglycosides should not be used on serious burns.  When applied locally, aminoglycosides are generally not absorbed to a significant extent through intact skin but readily absorbed through denuded, burned, or granulating skin.  Systemic adverse effects of aminoglycosides include nephrotoxicity and ototoxicity to vestibular and auditory functions.  In general, topical aminoglycosides should not be used on large areas of the body or for more than 7 days without medical supervision.', '2', '"Product Information. Myciguent (neomycin topical)." Pharmacia and Upjohn  (2001):', 'DDInter', 0);
