INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (54539, 19851, 'Glipizide', 'Glucosephosphate Dehydrogenase Deficiency', 'Sulfonylureas can cause hemolytic anemia in patients with glucose 6-phosphate dehydrogenase (G6PD) deficiency.  Therapy with these agents should be used with caution in patients with G6PD deficiency and consider the use of a non-sulfonylurea alternative.  There have been postmarketing reports of hemolytic anemia in patients receiving these drugs who did not have known G6PD deficiency.', '2', '"Product Information. Diabinese (chlorpropamide)." Pfizer U.S. Pharmaceuticals  (2002):|"Product Information. Glucotrol (glipizide)." Pfizer U.S. Pharmaceuticals  (2002):|"Product Information. Diabeta (glyburide)." Hoechst Marion-Roussel Inc, Kansas City, MO.|"Product Information. Micronase (glyburide)." Pharmacia and Upjohn  (2002):|"Product Information. Amaryl (glimepiride)." Hoechst Marion Roussel  (2001):|"Product Information. Tolinase (tolazamide)." Pharmacia and Upjohn  (2001):|"Product Information. Orinase (tolbutamide)." Pharmacia and Upjohn  (2001):|"Product Information. Dymelor (acetohexamide)." Lilly, Eli and Company  (2001):', 'DDInter', 0);
