INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (54955, 0, 'Hemin', 'Blood Coagulation Disorders, Inherited', 'Hemin for injection has exhibited transient, mild anticoagulant effects during clinical studies, therefore, concurrent anticoagulant therapy should be avoided.  The extent and duration of the hypocoagulable state induced by hemin has not been established.  Caution should be exercised when using hemin therapy in patients with coagulation defects or risk of bleeding.', '2', '"Product Information. Panhematin (hemin)." Recordati Rare Diseases Inc  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (54956, 5905, 'Haloperidol', 'Dementia', 'Older patients with dementia-related psychosis treated with antipsychotic drugs are at an increased risk of death; although the causes were varied, most of the deaths appeared to be either cardiovascular (e.g., heart failure, sudden death) or infectious (e.g., pneumonia) in nature.  A causal relationship with antipsychotic use has not been established.  In controlled trials in older patients with dementia-related psychosis, patients randomized to risperidone, aripiprazole, and olanzapine had higher incidence of cerebrovascular adverse events (e.g., stroke, transient ischemic attack), including fatalities, compared to patients treated with placebo.  These agents are not approved for the treatment of patients with dementia-related psychosis.', '3', '"Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals  (2001):|"Product Information. Risperdal (risperidone)." Janssen Pharmaceuticals  (2001):|"Product Information. Zyprexa (olanzapine)." Lilly, Eli and Company  (2001):|"Product Information. Seroquel (quetiapine)." Astra-Zeneca Pharmaceuticals  (2001):|"Product Information. Geodon (ziprasidone)." Pfizer U.S. Pharmaceuticals  (2001):|"Product Information. Abilify (aripiprazole)." Bristol-Myers Squibb  (2002):|"Product Information. Invega (paliperidone)." Janssen Pharmaceuticals  (2007):|"Product Information. Fanapt (iloperidone)." Vanda Pharmaceuticals Inc  (2009):|"Product Information. Saphris (asenapine)." Schering-Plough Corporation  (2009):|"Product Information. Latuda (lurasidone)." Sunovion Pharmaceuticals Inc  (2010):|"Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc  (2015):|"Product Information. Vraylar (cariprazine)." Actavis Pharma, Inc.  (2015):|"Product Information. Nuplazid (pimavanserin)." Accelis Pharma  (2016):|"Product Information. Caplyta (lumateperone)." Intra-Cellular Therapies, Inc.  (2022):', 'DDInter', 0);
