INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27015, 6186, 'Bempedoic acid', 'Kidney Diseases', 'There is limited experience with the use of bempedoic acid in patients with severe renal impairment, and the drug has not been studied in patients with end-stage renal disease (ESRD) receiving dialysis.  Caution is advised if used in these patients.', '2', '"Product Information. Nexletol (bempedoic acid)." Esperion Therapeutics  (2020):', 'DDInter', 0);
