INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27075, 2472, 'Telmisartan', 'Liver Diseases', 'Telmisartan is primarily eliminated by biliary excretion, and only a minute amount is excreted in the urine.  Dosage adjustments are not necessary in patients with renal impairment unless they are also volume-depleted, in which case therapy should be initiated under medical supervision.  Patients with hepatic impairment or biliary obstruction may require a dosage adjustment.  The manufacturer recommends administering telmisartan therapy with caution in patients with liver disease or biliary obstruction.', '2', '"Product Information. Micardis (telmisartan)." Boehringer-Ingelheim  (2001):|McClellan KJ, Markham A "Telmisartan." Drugs 56 (1998):  1039-44', 'DDInter', 0);
