INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27120, 16233, 'Temozolomide', 'Bone Marrow Failure Disorders', 'Patients treated with temozolomide may experience myelosuppression, including prolonged pancytopenia, which may result in aplastic anemia, which in some cases has resulted in a fatal outcome.  Prior to dosing, patients must have an absolute neutrophil count (ANC) greater than or equal to 1.5  109/L and a platelet count greater than or equal to 100  109/L.  A complete blood count should be obtained on Day 22 (21 days after the first dose) or within 48 hours of that day, and weekly until the ANC is above 1.5  109/L and platelet count exceeds 100  109/L.  Therapy with temozolomide should be administered cautiously in patients with compromised bone marrow reserve.  Close clinical monitoring of hematopoietic function is recommended.', '2', '"Product Information. Temodar (temozolomide)." Schering Corporation  (2001):', 'DDInter', 0);
