INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27128, 16233, 'Temozolomide', 'Kidney Diseases', 'Temozolomide population pharmacokinetic analysis indicated that creatinine clearance over the range of 36 to 130 mL/min/m2 has no effect on its clearance after oral administration.  The pharmacokinetics of temozolomide have not been studied in patients with severely impaired renal function (CrCl <36 mL/min/m2).  Caution should be exercised when temozolomide is administered to patients with severe renal impairment.  Temozolomide has not been studied in patients on dialysis.', '2', '"Product Information. Temodar (temozolomide)." Schering Corporation  (2001):', 'DDInter', 0);
