INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27351, 7196, 'Testosterone', 'Hypercalcemia', 'Androgenic anabolic steroids decrease urinary excretion of calcium, which may result in hypercalcemia.  Therapy with androgenic agents should be administered cautiously in patients with hypercalcemia, disorders of bone metabolism such as hyperparathyroidism, or renal dysfunction.  Some manufacturers consider the use of androgenic anabolic steroids to be contraindicated in patients with hypercalcemia.', '2', '"Product Information. Halotestin (fluoxymesterone)." Pharmacia and Upjohn  (2001):|"Product Information. Androderm (testosterone topical)." SmithKline Beecham  (2001):', 'DDInter', 0);
