INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27357, 7196, 'Testosterone', 'Thyroid Diseases', 'When androgenic anabolic steroids are used in patients with thyroid disorders, clinicians should be aware that these agents may affect thyroid function tests.  Specifically, thyroid-binding globulin levels may be decreased, resulting in lower total T4 serum levels and increased resin uptake of T3 and T4.  Free thyroid hormone levels remain unchanged, however.', '1', '"Product Information. Halotestin (fluoxymesterone)." Pharmacia and Upjohn  (2001):|"Product Information. Androderm (testosterone topical)." SmithKline Beecham  (2001):', 'DDInter', 0);
