INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55066, 5905, 'Haloperidol', 'Long QT Syndrome', 'Cases of sudden death, QT interval prolongation and ventricular arrhythmias including Torsade de Pointes have been reported in patients using haloperidol.  Although cases have been reported even in the absence of predisposing factors, special caution is advised in treating patients with other QT- prolonging conditions (including electrolyte imbalance such as hypokalemia and hypomagnesemia), using QT- prolonging drugs, with underlying cardiac abnormalities, hypothyroidism and familial long QT syndrome.  Consider periodic EKG assessment on these patients.  Treatment should be discontinued and a cardiac evaluation should be considered if a patient develops signs or symptoms of ventricular arrhythmia.', '2', '"Product Information. Haldol (haloperidol)." McNeil Pharmaceutical  (2002):', 'DDInter', 0);
