INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55151, 12915, 'Goserelin', 'Cardiovascular Diseases', 'Increased risk of developing myocardial infarction, sudden cardiac death and stroke has been reported in association with use of GnRH agonists such as goserelin in men.  The risk appears low based on the reported odds ratios, and should be evaluated carefully along with cardiovascular risk factors when determining a treatment for patients with prostate cancer.', '2', '"Product Information. Zoladex (goserelin)." Astra-Zeneca Pharmaceuticals  (2001):', 'DDInter', 0);
