INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55153, 12915, 'Goserelin', 'Hyperglycemia', 'Hyperglycemia and an increased risk of developing diabetes have been reported in men receiving GnRH agonists such as goserelin.  Hyperglycemia may represent development of diabetes mellitus or worsening of glycemic control in patients with diabetes.  Monitor blood glucose and/or glycosylated hemoglobin (HbA1c) periodically in patients receiving goserelin and manage with current practice for treatment of hyperglycemia or diabetes.', '2', '"Product Information. Zoladex (goserelin)." Astra-Zeneca Pharmaceuticals  (2001):', 'DDInter', 0);
