INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55595, 880, 'Hydralazine', 'Coronary Artery Disease', 'The use of hydralazine is contraindicated in patients with coronary artery disease.  Reflex tachycardia may commonly occur.  Palpitations and chest pain have also been reported.  Myocardial infarction has been associated with the use of hydralazine.', '3', 'Koch-Weser J "Hydralazine." N Engl J Med 295 (1976):  320-3|Laslett LJ, DeMaria AN, Amsterdam EA, Mason DT "Hydralazine-induced tachycardia and sodium retention in heart failure: hemodynamic and symptomatic correlation by prazosin therapy." Arch Intern Med 138 (1978):  819-20|Packer M, Meller J, Medina N, et al. "Provocation of myocardial ischemic events during initiation of vasodilator therapy for severe chronic heart failure." Am J Cardiol 48 (1981):  939-46', 'DDInter', 0);
