INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55600, 880, 'Hydralazine', 'Lupus Erythematosus, Systemic', 'The use of hydralazine has been associated with the development of lupus erythematosus and lupus-like syndromes, as well as exacerbation of the disease.  Hydralazine therapy should be withdrawn in patients experiencing worsening of preexisting lupus.  Monitoring complete blood counts, and antinuclear antibody titers before and during prolonged therapy is recommended.', '3', 'Hahn BH, Sharp GC, Irvin WS, et al. "Immune responses to hydralazine and nuclear antigens in hydralazine-induced lupus erythematosus." Ann Intern Med 76 (1972):  365-74|Carey RM, Coleman M, Feder A "Pericardial tamponade: a major presenting manifestation of hydralazine-induced lupus syndrome." Am J Med 54 (1973):  84-7|Perry HM "Late toxicity to hydralazine resembling systemic lupus erythematosus or rheumatoid arthritis." Am J Med 54 (1973):  58-72|Blumenkrantz N, Christiansen AH, Ullman S, Asboe-Hansen G "Hydralazine-induced lupoid syndrome." Acta Med Scand 195 (1974):  443-9|Weinstein J "Hypocomplementemia in hydralazine-associated systemic lupus erythematosus." Am J Med 65 (1978):  553-6|Freestone S, Ramsay LE "Transient monoclonal gammopathy in hydralazine-induced lupus erythematosus." Br Med J 285 (1982):  1536-7|Macleod WN "Anaemia in the hydrallazine-induced lupus syndrome." Scott Med J 28 (1983):  181-2|Ihle BU, Whitworth JA, Dowling JP, Kincaid-Smith P "Hydralazine and lupus nephritis." Clin Nephrol 22 (1984):  230-8|Cameron HA, Ramsay LE "The lupus syndrome induced by hydralazine: a common complication with low dose treatment." Br Med J 289 (1984):  410-12|Naparstek Y, Kopolovic J, Tur-Kaspa R, Rubinger D "Focal glumerulonephritis in the course of hydralazine-induced lupus syndrome." Arthritis Rheum 27 (1984):  822-5|Shapiro KS, Pinn VW, Harrington JT, Levey AS "Immune comp