INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55696, 651, 'Ampicillin', 'Heart Failure', 'Parenteral ampicillin sodium contains approximately 67 to 71 mg (2.9 to 3.1 mEq) of sodium per each gram of ampicillin activity.  The combination, ampicillin-sulbactam, contains approximately 115 mg (5 mEq) of sodium per 1.5 gram of total drug.  The sodium content should be considered when these products are used in patients with conditions that may require sodium restriction, such as congestive heart failure, hypertension, and fluid retention.', '2', '"Product Information. Omnipen (ampicillin)." Wyeth-Ayerst Laboratories  (2002):|"Product Information. Unasyn (ampicillin-sulbactam)." Roerig Division  (2002):', 'DDInter', 0);
