INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55728, 651, 'Ampicillin', 'Kidney Diseases', 'Most beta-lactam antibiotics are eliminated by the kidney as unchanged drug and, in some cases, also as metabolites.  The serum concentrations of beta-lactam antibiotics and their metabolites may be increased, and the half-lives prolonged, in patients with impaired renal function.  Neurotoxic reactions (e.g., encephalopathy, asterixis, myoclonus, seizures, coma) have been reported in such patients treated parenterally with these agents.  Dosage adjustments may be necessary, and modifications should be based on the degree of renal impairment as well as severity of infection in accordance with the individual product package labeling.  Renal function tests should be performed periodically during prolonged and/or high-dose therapy since nephrotoxicity and alterations in renal function have occasionally been associated with the use of these drugs.', '2', '"Product Information. Omnipen (ampicillin)." Wyeth-Ayerst Laboratories  (2002):|"Product Information. Ancef (cefazolin)." SmithKline Beecham  (2002):|"Product Information. Zefazone (cefmetazole)." Pharmacia and Upjohn  (2002):|"Product Information. Monocid (cefonicid)." SmithKline Beecham  (2002):|"Product Information. Claforan (cefotaxime)." Hoechst Marion Roussel  (2002):|"Product Information. Cefotan (cefotetan)." Stuart Pharmaceuticals  (2002):|"Product Information. Mefoxin (cefoxitin)." Merck & Co., Inc  (2002):|"Product Information. Fortaz (ceftazidime)." Glaxo Wellcome  (2002):|"Product Information. Tazicef (ceftazidime)." SmithKline Beecham  (2002):|"Product Information. Cefizox (ceftizoxime)." Fujisawa  (2002):|"Product Information. Ceftin (cefuroxime)." Glaxo Wellcome  (2002):|"Product Information. Zinacef (cefuroxime)." Glaxo Wellcome  (2002):|"Product Information. Keflin (cephalothin)." Lilly, Eli and Company  (2002):|"Product Information. Cefadyl (cephapirin)." Apothecon Inc  (2002):|"Product Information. Staphcillin (methicillin)." Apothecon Inc  (2002):|"Product Information. Pfizerpen (penicillin)." Roerig Division  (2001):|"Product Information. Pipracil (piperacillin)." Lederle Laboratories  (2001):|"Product Information. Ticar (ticarcillin)." SmithKline Beecham  (2001):|"Product Information. Mandol (cefamandole)." Lilly, Eli and Company  (2001):|"Product Information. Maxipime (cefepime)." Bristol-Myers Squibb  (2001):|"Product Information. Fetroja (cefiderocol)." Shionogi USA Inc  (2019):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55729, 653, 'Ampicillin', 'Kidney Diseases', 'Most beta-lactam antibiotics are eliminated by the kidney as unchanged drug and, in some cases, also as metabolites.  The serum concentrations of beta-lactam antibiotics and their metabolites may be increased, and the half-lives prolonged, in patients with impaired renal function.  Neurotoxic reactions (e.g., encephalopathy, asterixis, myoclonus, seizures, coma) have been reported in such patients treated parenterally with these agents.  Dosage adjustments may be necessary, and modifications should be based on the degree of renal impairment as well as severity of infection in accordance with the individual product package labeling.  Renal function tests should be performed periodically during prolonged and/or high-dose therapy since nephrotoxicity and alterations in renal function have occasionally been associated with the use of these drugs.', '2', '"Product Information. Omnipen (ampicillin)." Wyeth-Ayerst Laboratories  (2002):|"Product Information. Ancef (cefazolin)." SmithKline Beecham  (2002):|"Product Information. Zefazone (cefmetazole)." Pharmacia and Upjohn  (2002):|"Product Information. Monocid (cefonicid)." SmithKline Beecham  (2002):|"Product Information. Claforan (cefotaxime)." Hoechst Marion Roussel  (2002):|"Product Information. Cefotan (cefotetan)." Stuart Pharmaceuticals  (2002):|"Product Information. Mefoxin (cefoxitin)." Merck & Co., Inc  (2002):|"Product Information. Fortaz (ceftazidime)." Glaxo Wellcome  (2002):|"Product Information. Tazicef (ceftazidime)." SmithKline Beecham  (2002):|"Product Information. Cefizox (ceftizoxime)." Fujisawa  (2002):|"Product Information. Ceftin (cefuroxime)." Glaxo Wellcome  (2002):|"Product Information. Zinacef (cefuroxime)." Glaxo Wellcome  (2002):|"Product Information. Keflin (cephalothin)." Lilly, Eli and Company  