INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55760, 651, 'Ampicillin', 'Diseases requiring hemodialysis', 'Penicillin antibiotics (except for agents in the penicillinase-resistant class) are removed by hemodialysis.  Doses should either be scheduled for administration after dialysis or supplemental doses be given after dialysis.', '2', 'Giron JA, Meyers BR, Hirschman SZ, Srulevitch E "Pharmacokinetics of piperacillin in patients with moderate renal failure and in patients undergoing hemodialysis." Antimicrob Agents Chemother 19 (1981):  279-83|Heim KL "The effect of hemodialysis on piperacillin pharmacokinetics." Drug Intell Clin Pharm 19 (1985):  455|Francke EL, Appel GB, Neu HC "Kinetics of intravenous amoxicillin in patients on long-term dialysis." Clin Pharmacol Ther 26 (1979):  31-5|Slaughter RL, Kohli R, Brass C "Effects of hemodialysis on the pharmacokinetics of amoxicillin/clavulanic acid combination." Ther Drug Monit 6 (1984):  424-7|Janicke DM, Mangione A, Schultz RW, Jusko WJ "Mezlocillin disposition in chronic hemodialysis patients." Antimicrob Agents Chemother 20 (1981):  590-4|Kampf D, Schurig R, Weihermuller K, Forster D "Effects of impaired renal function hemodialysis and peritoneal dialysis on the pharmacokinetics of mezlocillin." Antimicrob Agents Chemother 18 (1980):  81-7|Davies BE, Boon R, Horton R, Reubi FC, Descoeudres CE "Pharmacokinetics of amoxycillin and clavulanic acid in haemodialysis patients following intravenous administration of augmentin." Br J Clin Pharmacol 26 (1988):  385-90|Francke E, Mehta S, Neu HC, Appel GB "Kinetics of intravenous mezlocillin in chronic hemodialysis patients." Clin Pharmacol Ther 26 (1979):  228-31|Thorsteinsson SB, Steingrimsson O, Asmundsson P, Bergan T "Pharmacokinetics of mezlocillin during haemodialysis." Scand J Infect Dis 29 (1981):  59-63|Wise R, Reeves DS, Parker AS "Administration of ticarc