INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55861, 6276, 'Hydroxyurea', 'Bone Marrow Failure Disorders', 'The use of hydroxyurea is contraindicated in patients with marked myelosuppression.  Hydroxyurea induces myelosuppression, primarily leukopenia.  Thrombocytopenia and anemia occur less frequently.  Patients should be instructed to immediately report any signs or symptoms suggesting bone marrow suppression such as fever, sore throat, local infection, or bleeding.  Close clinical monitoring of hematopoietic function is recommended.  Hydroxyurea should not be administered if the white blood cell count falls below 2500/mm3 and/or the platelet count falls below 100,000/mm3, or in the presence of severe anemia.', '3', '"Product Information. Hydrea (hydroxyurea)." Bristol-Myers Squibb  (2001):', 'DDInter', 0);
