INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55947, 1062, 'Hydroxyzine', 'Asthma', 'It has been suggested that the anticholinergic effect of antihistamines may reduce the volume and cause thickening of bronchial secretions, resulting in obstruction of respiratory tract.  Some manufacturers and clinicians recommend that therapy with antihistamines be administered cautiously in patients with asthma or chronic obstructive pulmonary disease.', '2', '"Product Information. Dimetane (brompheniramine)." Wyeth-Ayerst Laboratories  (2002):|"Product Information. Chlor-Trimeton (chlorpheniramine)." Schering-Plough|"Product Information. Periactin (cyproheptadine)." Merck & Co., Inc  (2002):|"Product Information. Benadryl (diphenhydramine)." Parke-Davis  (2002):|"Product Information. Phenergan (promethazine)." Wyeth-Ayerst Laboratories  (2001):|Maddox DE, Reed CE "Clinical pharmacodynamics of antihistamines." Ann Allergy 59 (1987):  43-8|"Product Information. Tavist (clemastine)." Sandoz Pharmaceuticals Corporation  (2001):|"Product Information. Antivert (meclizine)." Roerig Division  (2001):|"Product Information. Marezine (cyclizine)." Glaxo Wellcome  (2001):|"Product Information. Optimine (azatadine)." Schering Corporation  (2001):|"Product Information. Semprex-D (acrivastine-pseudoephedrine)." Endo Laboratories LLC  (2001):|"Product Information. Drixoral (dextromethorphan)." Schering-Plough  (2001):|"Product Information. Poly-Histine-D (pyrilamine)." Bock Pharmacal Company  (2001):|"Product Information. Vistaril (hydroxyzine)." Pfizer U.S. Pharmaceuticals  (2001):|"Product Information. Dramamine (dimenhydrinate)." Pharmacia and Upjohn  (2001):|"Product Information. Tacaryl (methdilazine)." Westwood Squibb Pharmaceutical Corporation  (2001):|"Product Information. Temaril (trimeprazine)." Allergan Inc  (2001):', 'DDInter', 0);
