INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55981, 20724, 'Ibrutinib', 'Tumor Lysis Syndrome', 'Tumor lysis syndrome has been reported with ibrutinib therapy.  It is recommended to assess the baseline risk (e.g., high tumor burden) and take appropriate precautions.  Monitor patients closely and treat as appropriate.', '2', '"Product Information. Imbruvica (ibrutinib)." Pharmacyclics Inc  (2013):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55982, 20725, 'Ibrutinib', 'Tumor Lysis Syndrome', 'Tumor lysis syndrome has been reported with ibrutinib therapy.  It is recommended to assess the baseline risk (e.g., high tumor burden) and take appropriate precautions.  Monitor patients closely and treat as appropriate.', '2', '"Product Information. Imbruvica (ibrutinib)." Pharmacyclics Inc  (2013):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55983, 20724, 'Ibrutinib', 'Lung Diseases', 'The use of certain multikinase inhibitors has been associated with pulmonary toxicity.  Serious cases of interstitial lung disease (ILD), including fatal cases and interstitial pneumonitis or pulmonary fibrosis have been reported.  Caution is recommended when using these agents in patients with a history of interstitial pneumonitis or pulmonary fibrosis or those patients presenting with acute onset of new or progressive unexplained pulmonary symptoms such as dyspnea, cough, and fever pending diagnostic evaluation.  If ILD is confirmed, permanently discontinue these agents and institute appropriate measures.  Immediately withhold treatment in patients diagnosed with ILD/pneumonitis and permanently discontinue therapy if no other potential causes of ILD/pneumonitis have been identified.', '2', '"Product Information. Vandetanib (vandetanib)." Astra-Zeneca Pharmaceuticals  (2011):|"Product Information. Zelboraf (vemurafenib)." Genentech  (2011):|"Product Information. Xalkori (crizotinib)." Pfizer U.S. Pharmaceuticals Group  (2011):|"Product Information. Mekinist (trametinib)." GlaxoSmithKline  (2013):|"Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals  (2014):|"Product Information. Zydelig (idelalisib)." Gilead Sciences  (2014):|"Product Information. Alecensa (alectinib)." Genentech  (2015):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55984, 20725, 'Ibrutinib', 'Lung Diseases', 'The use of certain multikinase inhibitors has been associated with pulmonary toxicity.  Serious cases of interstitial lung disease (ILD), including fatal cases and interstitial pneumonitis or pulmonary fibrosis have been reported.  Caution is recommended when using these agents in patients with a history of interstitial pneumonitis or pulmonary fibrosis or those patients presenting with acute onset of new or progressive unexplained pulmonary symptoms such as dyspnea, cough, and fever pending diagnostic evaluation.  If ILD is confirmed, permanently discontinue these agents and institute appropriate measures.  Immediately withhold treatment in patients diagnosed with ILD/pneumonitis and permanently discontinue therapy if no other potential causes of ILD/pneumonitis have been identified.', '2', '"Product Information. Vandetanib (vandetanib)." Astra-Zeneca Pharmaceuticals  (2011):|"Product Information. Zelboraf (vemurafenib)." Genentech  (2011):|"Product Information. Xalkori (crizotinib)." Pfizer U.S. Pharmaceuticals Group  (2011):|"Product Information. Mekinist (trametinib)." GlaxoSmithKline  (2013):|"Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals  (2014):|"Product Information. Zydelig (idelalisib)." Gilead Sciences  (2014):|"Product Information. Alecensa (alectinib)." Genentech  (2015):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55985, 10785, 'Hyoscyamine', 'Diabetic Neuropathies', 'Agents with anticholinergic activity can exacerbate many of the manifestations of autonomic neuropathy, including tachycardia, anhidrosis, bladder atony, obstipation, dry mouth and eyes, cycloplegia and blurring of vision, and sexual impotence in males.  Therapy with antimuscarinic agents and higher dosages of antispasmodic agents (e.g., dicyclomine or oxybutynin) should be administered cautiously in patients with autonomic neuropathy.', '3', '"Product Information. Atropine Sulfate (atropine)." ESI Lederle Generics  (2022):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55986, 10785, 'Hyoscyamine', 'Intestinal Obstruction', 'Anticholinergics are contraindicated in patients with obstructive diseases such as achalasia, esophageal stricture or stenosis, pyloroduodenal stenosis, stenosing peptic ulcer, pyloric obstruction, and paralytic ileus.  Anticholinergics may further suppress intestinal motility with resultant precipitation or aggravation of toxic megacolon.', '3', 'Bantz EW, Dolen WK, Chadwick EW, Nelson HS "Chronic chlorpheniramine therapy: subsensitivity, drug metabolism, and compliance." Ann Allergy 59 (1987):  341-6|Simons FE, Frith EM, Simons KJ "The pharmacokinetics and antihistaminic effects of brompheniramine." J Allergy Clin Immunol 70 (1982):  458-64|Blamoutier J "Comparative trial of two antihistamines, mequitazine and brompheniramine." Curr Med Res Opin 5 (1978):  366-70|"Azatadine (optimine)--a new antihistamine." Med Lett Drugs Ther 19 (1977):  77-9|"Product Information. Dimetane (brompheniramine)." Wyeth-Ayerst Laboratories  (2002):|"Product Information. Chlor-Trimeton (chlorpheniramine)." Schering-Plough|"Product Information. Periactin (cyproheptadine)." Merck & Co., Inc  (2002):|"Product Information. Benadryl (diphenhydramine)." Parke-Davis  (2002):|"Product Information. Phenergan (promethazine)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Tavist (clemastine)." Sandoz Pharmaceuticals Corporation  (2001):|"Product Information. Antivert (meclizine)." Roerig Division  (2001):|"Product Information. Optimine (azatadine)." Schering Corporation  (2001):|Mevorach D "Adverse effects of atropine sulfate autoinjection." Ann Pharmacother 26 (1992):  564|"Product Information. Atropine Sulfate (atropine)." ESI Lederle Generics  (2022):|"Product Information. Artane (trihexyphenidyl)." Lederle Laboratories  (2001):|"Product Information. Poly-Histine-D (pyrilamine)." Bock Pharmacal Company  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55987, 10785, 'Hyoscyamine', 'Glaucoma', 'Anticholinergic agents are contraindicated in patients with primary glaucoma, a tendency toward glaucoma (narrow anterior chamber angle), or adhesions (synechiae) between the iris and lens, as well as for the elderly and others in whom undiagnosed glaucoma or excessive pressure in the eye may be present.  Because anticholinergics cause mydriasis, they may exacerbate these conditions.', '3', 'Schuller DE, Turkewitz D "Adverse effects of antihistamines." Postgrad Med 79 (1986):  75-86|"Product Information. Dimetane (brompheniramine)." Wyeth-Ayerst Laboratories  (2002):|"Product Information. Chlor-Trimeton (chlorpheniramine)." Schering-Plough|"Product Information. Thorazine (chlorpromazine)." SmithKline Beecham  (2002):|"Product Information. Periactin (cyproheptadine)." Merck & Co., Inc  (2002):|"Product Information. Benadryl (diphenhydramine)." Parke-Davis  (2002):|"Product Information. Phenergan (promethazine)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Tavist (clemastine)." Sandoz Pharmaceuticals Corporation  (2001):|"Product Information. Optimine (azatadine)." Schering Corporation  (2001):|O''Connor PS, Mumma JV "Atropine toxicity." Am J Ophthalmol 99 (1985):  613-4|Clearkin LG "Angle closure glaucoma precipitated by atropine." Arch Intern Med 152 (1992):  880|Berdy GJ, Berdy SS, Odin LS, Hirst LW "Angle closure glaucoma precipitated by aerosolized atropine." Arch Intern Med 151 (1991):  1658-60|Pecora JL "Malignant glaucoma worsened by miotics in a postoperative angle- closure glaucoma patient." Ann Ophthalmol 11 (1979):  1412-4|Holland MG "Autonomic drugs in ophthalmology: some problems and promises. Section II: Anticholinergic drugs." Ann Ophthalmol 6 (1974):  661-4|Kanto J "New aspects in the use of atropine." Int J Clin Pharmacol Ther Toxicol 21 (1983):  92-4|"Product Information. Atropine Sulfate (atropine)." ESI Lederle Generics  (2022):|"Product Information. Compazine (prochlorperazine)." SmithKline Beecham  (2001):|Goldstein JH "Effects of drugs on cornea, conjunctiva, and lids." Int Ophthalmol Clin 11 (1971):  13-34|"Product Information. Cogentin (benztropine)." Merck & Co., Inc  (2001):|"Product Information. Artane (trihexyphenidyl)." Lederle Laboratories  (2001):|"Product Information. Moban (molindone)." Gate Pharmaceuticals  (2001):|"Product Information. Orap (pimozide)." Gate Pharmaceuticals|"Product Information. Poly-Histine-D (pyrilamine)." Bock Pharmacal Company  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55988, 10785, 'Hyoscyamine', 'Urinary Bladder Neck Obstruction', 'In general, the use of anticholinergic agents is contraindicated in patients with urinary retention and bladder neck obstruction caused by prostatic hypertrophy.  Dysuria may occur and may require catheterization.  Also, anticholinergic drugs may aggravate partial obstructive uropathy.  Caution is advised even when using agents with mild to moderate anticholinergic activity, particularly in elderly patients.', '3', 'Bantz EW, Dolen WK, Chadwick EW, Nelson HS "Chronic chlorpheniramine therapy: subsensitivity, drug metabolism, and compliance." Ann Allergy 59 (1987):  341-6|Schuller DE, Turkewitz D "Adverse effects of antihistamines." Postgrad Med 79 (1986):  75-86|"Product Information. Dimetane (brompheniramine)." Wyeth-Ayerst Laboratories  (2002):|"Product Information. Chlor-Trimeton (chlorpheniramine)." Schering-Plough|"Product Information. Thorazine (chlorpromazine)." SmithKline Beecham  (2002):|"Product Information. Periactin (cyproheptadine)." Merck & Co., Inc  (2002):|"Product Information. Benadryl (diphenhydramine)." Parke-Davis  (2002):|"Product Information. Phenergan (promethazine)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Tavist (clemastine)." Sandoz Pharmaceuticals Corporation  (2001):|"Product Information. Antivert (meclizine)." Roerig Division  (2001):|"Product Information. Optimine (azatadine)." Schering Corporation  (2001):|Shutt LE, Bowes JB "Atropine and hyoscine." Anaesthesia 34 (1979):  476-90|O''Kelly SW, Spargo PM "Postoperative urinary retention in men." BMJ 302 (1991):  1403-4|"Product Information. Atropine Sulfate (atropine)." ESI Lederle Generics  (2022):|"Product Information. Compazine (prochlorperazine)." SmithKline Beecham  (2001):|"Product Information. Zyrtec (cetirizine)." Pfizer U.S. Pharmaceuticals  (2001):|"Product Information. Artane (trihexyphenidyl)." Lederle Laboratories  (2001):|"Product Information. Moban (molindone)." Gate Pharmaceuticals  (2001):|"Product Information. Orap (pimozide)." Gate Pharmaceuticals|"Product Information. Poly-Histine-D (pyrilamine)." Bock Pharmacal Company  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55989, 10785, 'Hyoscyamine', 'Lung Diseases', 'The use of systemic anticholinergics is contraindicated in the treatment of lower respiratory tract symptoms including asthma.  Muscarinic receptor antagonists reduce bronchial secretions, which can result in decreased fluidity and increased thickening of secretions.  However, ipratropium does not produce these effects and can be used safely in treating asthma.', '3', 'Nahata MC, Clotz MA, Krogg EA "Adverse effects of meperidine, promethazine, and chlorpromazine for sedation in pediatric patients." Clin Pediatr (Phila) 24 (1985):  558-60|"Product Information. Phenergan (promethazine)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Antivert (meclizine)." Roerig Division  (2001):|"Product Information. Marezine (cyclizine)." Glaxo Wellcome  (2001):|"Product Information. Atropine Sulfate (atropine)." ESI Lederle Generics  (2022):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55990, 10785, 'Hyoscyamine', 'Myasthenia Gravis', 'Because antimuscarinic agents have anticholinergic effects, they are contraindicated in patients with myasthenia gravis.  Their use may be appropriate to reduce adverse muscarinic effects caused by an anticholinesterase agent.', '3', 'Shutt LE, Bowes JB "Atropine and hyoscine." Anaesthesia 34 (1979):  476-90|"Product Information. Atropine Sulfate (atropine)." ESI Lederle Generics  (2022):|"Product Information. Bentyl (dicyclomine)." Aventis Pharmaceuticals  (2002):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55991, 10785, 'Hyoscyamine', 'Dysentery', 'The use of drugs with antiperistaltic activity (primarily antidiarrheal and antimuscarinic agents, but also antispasmodic agents such as dicyclomine or oxybutynin at high dosages) is contraindicated in patients with diarrhea due to pseudomembranous enterocolitis or enterotoxin-producing bacteria.  These drugs may prolong and/or worsen diarrhea associated with organisms that invade the intestinal mucosa, such as toxigenic E. coli, Salmonella and Shigella, and pseudomembranous colitis due to broad-spectrum antibiotics.  Other symptoms and complications such as fever, shedding of organisms and extraintestinal illness may also be increased or prolonged.  In general, because antiperistaltic agents decrease gastrointestinal motility, they may delay the excretion of infective gastroenteric organisms or toxins and should be used cautiously in patients with any infectious diarrhea, particularly if accompanied by high fever or pus or blood in the stool.  Some cough and cold and other combination products may occasionally include antimuscarinic agents for their drying effects and may, therefore, require careful selection when necessary.', '3', 'Brown JW "Toxic megacolon associated with loperamide therapy." JAMA 241 (1979):  501-2|Walley T, Milson D "Loperamide related toxic megacolon in Clostridium difficile colitis." Postgrad Med J 66 (1990):  582|"Product Information. Imodium (loperamide)." Janssen Pharmaceuticals  (2001):|Marshall WF Jr, Rosenthal P, Merritt RJ "Atropine therapy and paralytic ileus in an infant." J Pediatr Gastroenterol Nutr 9 (1989):  532-4|"Lomotil for diarrhea in children." Med Lett Drugs Ther 17 (1975):  104|"Product Information. Atropine Sulfate (atropine)." ESI Lederle Generics  (2022):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55992, 10785, 'Hyoscyamine', 'Heart Diseases', 'Anticholinergics block vagal inhibition of the SA nodal pacemaker.  Therapy with anticholinergics should be administered cautiously to patients with tachycardia, congestive heart failure, or coronary artery disease.  Premature ventricular depolarization, ventricular tachycardia, and fibrillation associated with anticholinergics are rare.', '2', 'Schuller DE, Turkewitz D "Adverse effects of antihistamines." Postgrad Med 79 (1986):  75-86|Lowenthal DT, Reidenberg MM "The heart rate response to atropine in uremic patients, obese subjects before and during fasting, and patients with other chronic illnesses." Proc Soc Exp Biol Med 139 (1972):  390-3|"Product Information. Benadryl (diphenhydramine)." Parke-Davis  (2002):|"Product Information. Tavist (clemastine)." Sandoz Pharmaceuticals Corporation  (2001):|"Product Information. Antivert (meclizine)." Roerig Division  (2001):|Lunde P "Ventricular fibrillation after intravenous atropine for treatment of sinus bradycardia." Acta Med Scand 199 (1976):  369-71|Cooper MJ, Abinader EG "Atropine-induced ventricular fibrillation: case report and review of the literature." Am Heart J 97 (1979):  225-8|Das G, Talmers FN, Weissler AM "New observations on the effects of atropine on the sinoatrial and atrioventricular nodes in man." Am J Cardiol 36 (1975):  281-5|Knoebel SB, McHenry PL, Phillips JF, Widlansky S "Atropine-induced cardioacceleration and myocardial blood flow in subjects with and without coronary artery disease." Am J Cardiol 33 (1974):  327-32|Bradshaw EG "Dysrhythmias associated with oral surgery." Anaesthesia 31 (1976):  13-7|Horgan J "Atropine and ventricular tachyarrhythmia." JAMA 223 (1973):  693|Zsigmond EK, Matsuki A, Sharafabadi C "Atropine and cardiac arrhythmia." N Engl J Med 288 (1973):  635|Massumi RA, Mason DT, Amsterdam EA, DeMaria A, Miller RR, Scheinman MM, Zelis R "Ventricular fibrillation and tachycardia after intravenous atropine for treatment of bradycardias." N Engl J Med 287 (1972):  336-8|Valentin N, Staffeldt H, Kyst A "Effect of i.v. atropine on cardiac rhythm, heart rate, blood pressure and airway secretion during isoflurane anaesthesia." Acta Anaesthesiol Scand 28 (1984):  621-4|Lazzari JO, Benchuga EG, Elizari MV, Rosenbaum MB "Ventricular fibrillation after intravenous atropine in a patient with atrioventricular block." Pacing Clin Electrophysiol 5 (1982):  196-200|"Product Information. Atropine Sulfate (atropine)." ESI Lederle Generics  (2022):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55993, 10785, 'Hyoscyamine', 'Tachycardia', 'Anticholinergics block vagal inhibition of the SA nodal pacemaker.  Therapy with anticholinergics should be administered cautiously in patients with tachycardia, congestive heart failure, or coronary artery disease.  Premature ventricular depolarization or ventricular tachycardia or fibrillation associated with anticholinergics is rare.', '2', '"Product Information. Antivert (meclizine)." Roerig Division  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55994, 10785, 'Hyoscyamine', 'Coronary Artery Disease', 'Antimuscarinic agents block vagal inhibition of the SA nodal pacemaker.  These agents should be administered cautiously in patients with tachycardia, congestive heart failure, or coronary artery disease.  Premature ventricular depolarization or ventricular tachycardia or fibrillation associated with antimuscarinic drugs is rare.', '2', 'Lunde P "Ventricular fibrillation after intravenous atropine for treatment of sinus bradycardia." Acta Med Scand 199 (1976):  369-71|Knoebel SB, McHenry PL, Phillips JF, Widlansky S "Atropine-induced cardioacceleration and myocardial blood flow in subjects with and without coronary artery disease." Am J Cardiol 33 (1974):  327-32|Richman S "Adverse effect of atropine during myocardial infarction. Enchancement of ischemia following intravenously administered atropine." JAMA 228 (1974):  1414-6|"Product Information. Atropine Sulfate (atropine)." ESI Lederle Generics  (2022):|"Product Information. Bentyl (dicyclomine)." Aventis Pharmaceuticals  (2002):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55995, 10785, 'Hyoscyamine', 'Stomach Ulcer', 'Antimuscarinic agents may cause a delay in gastric emptying and possibly antral stasis in patients with gastric ulcer.  Therapy with antimuscarinic agents should be administered cautiously to patients with gastric ulcer.', '2', 'Chernish SM, Brunelle RR, Rosenak BD, Ahmadzai S "Comparison of the effects of glucagon and atropine sulfate on gastric emptying." Am J Gastroenterol 70 (1978):  581-6|Mevorach D "Adverse effects of atropine sulfate autoinjection." Ann Pharmacother 26 (1992):  564|Cotton BR, Smith G "Single and combined effects of atropine and metoclopramide on the lower oesophageal sphincter pressure." Br J Anaesth 53 (1981):  869-74|"Product Information. Atropine Sulfate (atropine)." ESI Lederle Generics  (2022):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55996, 10785, 'Hyoscyamine', 'Gastroesophageal Reflux', 'Antimuscarinic agents decrease gastric motility and relax the lower esophageal sphincter which promotes gastric retention and can aggravate reflux.  These drugs should be administered cautiously in patients with gastroesophageal reflux or hiatal hernia associated with reflux esophagitis.', '2', 'Chernish SM, Brunelle RR, Rosenak BD, Ahmadzai S "Comparison of the effects of glucagon and atropine sulfate on gastric emptying." Am J Gastroenterol 70 (1978):  581-6|Dow TG, Brock-Utne JG, Rubin J, Welman S, Dimopoulos GE, Moshal MG "The effect of atropine on the lower esophageal sphincter in late pregnancy." Obstet Gynecol 51 (1978):  426-30|Howells TH "The administration of metoclopramide with atropine." Anaesthesia 32 (1977):  677|Brock-Utne JG, Rubin J, Downing JW, Dimopoulos GE, Moshal MG, Naicker M "The administration of metoclopramide with atropine. A drug interaction effect on the gastro-oesophageal sphincter in man." Anaesthesia 31 (1976):  1186-90|Cotton BR, Smith G "Single and combined effects of atropine and metoclopramide on the lower oesophageal sphincter pressure." Br J Anaesth 53 (1981):  869-74|"Product Information. Atropine Sulfate (atropine)." ESI Lederle Generics  (2022):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55997, 10785, 'Hyoscyamine', 'Colitis, Ulcerative', 'Antimuscarinic agents may suppress intestinal motility and produce paralytic ileus with resultant precipitation of toxic megacolon.  These drugs should be administered cautiously to patients with ulcerative colitis.', '2', 'Famewo CE "A re-evaluation of anticholergic premedication." Can Anaesth Soc J 24 (1977):  39-41|"Product Information. Atropine Sulfate (atropine)." ESI Lederle Generics  (2022):|Braunwald E, Hauser SL, Kasper DL, Fauci AS, Isselbacher KJ, Longo DL, Martin JB, eds., Wilson JD "Harrison''s Principles of Internal Medicine." New York, NY: McGraw-Hill Health Professionals Division  (1998):|"Product Information. Bentyl (dicyclomine)." Aventis Pharmaceuticals  (2002):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55998, 10785, 'Hyoscyamine', 'Liver Diseases', 'Atropine-like agents undergo significant hepatic metabolism.  Therapy with atropine-like agents should be administered cautiously to patients with liver disease.', '2', '"Product Information. Atropine Sulfate (atropine)." ESI Lederle Generics  (2022):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55999, 10785, 'Hyoscyamine', 'Renal Insufficiency', 'Atropine-like agents are primarily eliminated by the kidney.  Therapy with atropine-like agents should be administered cautiously to patients with renal disease.', '2', '"Product Information. Atropine Sulfate (atropine)." ESI Lederle Generics  (2022):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (56000, 10785, 'Hyoscyamine', 'Hypertension', 'Cardiovascular effects of anticholinergics may exacerbate hypertension.  Therapy with anticholinergic agents should be administered cautiously in patients with hypertension.', '1', '"Product Information. Benadryl (diphenhydramine)." Parke-Davis  (2002):|"Product Information. Antivert (meclizine)." Roerig Division  (2001):|"Product Information. Marezine (cyclizine)." Glaxo Wellcome  (2001):|Valentin N, Staffeldt H, Kyst A "Effect of i.v. atropine on cardiac rhythm, heart rate, blood pressure and airway secretion during isoflurane anaesthesia." Acta Anaesthesiol Scand 28 (1984):  621-4|"Product Information. Atropine Sulfate (atropine)." ESI Lederle Generics  (2022):|"Product Information. Artane (trihexyphenidyl)." Lederle Laboratories  (2001):|"Product Information. Atropisol (atropine ophthalmic)." Ciba Vision Ophthalmics  (2002):', 'DDInter', 0);