INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27580, 23204, 'Thalidomide', 'Hypotension', 'The use of thalidomide has been associated with dizziness and orthostatic hypotension.  Therapy with thalidomide should be administered cautiously in patients with preexisting hypotension.  Rising slowly from a recumbent position is recommended.', '2', '"Product Information. Thalomid (thalidomide)." Celgene Corporation  (2001):', 'DDInter', 0);
