INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27583, 23204, 'Thalidomide', 'Neutropenia', 'The use of thalidomide has been associated with decreased white blood cells counts, including neutropenia.  Therapy with thalidomide should be administered cautiously in patients with preexisting neutropenia.  Treatment should not be initiated if absolute neutrophil count is below 750/mm3.  Monitoring white blood cell count and differential routinely is recommended during treatment.', '2', '"Product Information. Thalomid (thalidomide)." Celgene Corporation  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27584, 28097, 'Thalidomide', 'Neutropenia', 'The use of thalidomide has been associated with decreased white blood cells counts, including neutropenia.  Therapy with thalidomide should be administered cautiously in patients with preexisting neutropenia.  Treatment should not be initiated if absolute neutrophil count is below 750/mm3.  Monitoring white blood cell count and differential routinely is recommended during treatment.', '2', '"Product Information. Thalomid (thalidomide)." Celgene Corporation  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27585, 28098, 'Thalidomide', 'Neutropenia', 'The use of thalidomide has been associated with decreased white blood cells counts, including neutropenia.  Therapy with thalidomide should be administered cautiously in patients with preexisting neutropenia.  Treatment should not be initiated if absolute neutrophil count is below 750/mm3.  Monitoring white blood cell count and differential routinely is recommended during treatment.', '2', '"Product Information. Thalomid (thalidomide)." Celgene Corporation  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27586, 23204, 'Thalidomide', 'Peripheral Nervous System Diseases', 'The use of thalidomide has been associated with the development of peripheral neuropathy, which is sometimes irreversible.  Therapy with thalidomide should be administered cautiously in patients with preexisting neuropathy and only after the neuropathy has returned to baseline.  Patients should be monitored routinely for early signs of neuropathy including numbness, tingling, pain, or a burning sensation in the hands or feet.  If symptoms of drug-induced neuropathy develop, thalidomide should be discontinued immediately.', '2', '"Product Information. Thalomid (thalidomide)." Celgene Corporation  (2001):', 'DDInter', 0);
