INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27597, 11105, 'Tetracosactide', 'Adrenocortical Hyperfunction', 'The use of adrenocorticotropic agents is contraindicated in patients with adrenocortical hyperfunction.  These agents stimulate the release of cortisol and aldosterone and may aggravate symptoms of hyperadrenocorticism.', '3', '"Product Information. Acthar (corticotropin)." Rhone Poulenc Rorer  (2001):|"Product Information. Acthrel (corticorelin)." Ferring Pharmaceuticals Inc  (2001):|"Product Information. Cortrosyn (cosyntropin)." Organon  (2001):', 'DDInter', 0);
