INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27878, 10813, 'Tiotropium', 'Angioedema', 'Angioedema has been reported during therapy with inhaled anticholinergic agents.  Therapy with these agents should be administered cautiously in patients at risk of developing angioedema and if such reaction occurs during treatment, therapy should be stopped at once and alternative treatments should be considered.  In addition, therapy with Spiriva HandiHaler should be used with caution in patients with severe hypersensitivity to milk proteins as the product contains the inactive ingredient, lactose monohydrate.', '2', '"Product Information. Atrovent (ipratropium)." Boehringer-Ingelheim  (2002):|"Product Information. Spiriva (tiotropium)." Boehringer Ingelheim  (2002):', 'DDInter', 0);
