INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27922, 8509, 'Ticagrelor', 'Bradycardia', 'Ticagrelor can cause ventricular pauses.  Bradyarrhythmias including AV block have been reported in the postmarketing setting.  It is recommended to use care if prescribing this agent to patients with a history of sick sinus syndrome, 2nd or 3rd degree AV block or bradycardia-related syncope not protected by a pacemaker as they may be at increased risk of developing bradyarrhythmias.', '2', '"Product Information. Brilinta (ticagrelor)." Astra-Zeneca Pharmaceuticals  (2011):', 'DDInter', 0);
