INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27943, 0, 'Tilmanocept', 'Hypersensitivity', 'Tilmanocept may pose a risk of hypersensitivity reactions due to its chemical similarity to dextran and modified forms of dextran (such as iron dextran drugs).  Before administering, ask patients about prior hypersensitivity reactions to drugs, especially to dextran and modified forms of dextran.  It is recommended to have resuscitation equipment and trained personnel immediately available at the time of administration.', '2', '"Product Information. Lymphoseek (tilmanocept)." Navidea Biopharmaceuticals  (2013):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27944, 4704, 'Tinidazole', 'Hematologic Diseases', 'The use of nitroimidazoles (e.g., metronidazole, tinidazole) has rarely been associated with hematologic adverse effects such as mild, transient leukopenia, thrombocytopenia, and bone marrow aplasia.  The manufacturers recommend that therapy with nitroimidazoles be administered cautiously in patients with evidence of or a history of blood dyscrasias, and that total and differential leukocyte counts be performed before and after treatment with these drugs, particularly in patients receiving repeated courses of therapy.', '3', 'Smith JA "Neutropenia associated with metronidazole therapy." Can Med Assoc J 123 (1980):  202|White CM, Price JJ, Hunt KM "Bone marrow aplasia associated with metronidazole." Br Med J 280 (1980):  647|"Product Information. Flagyl (metronidazole)." Searle  (2002):|"Product Information. Tindamax (tinidazole)." Presutti Laboratories Inc  (2004):', 'DDInter', 0);
