INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27948, 4704, 'Tinidazole', 'Alcoholism', 'Nitroimidazoles (e.g., metronidazole, tinidazole, fexinidazole, secnidazole) may inhibit alcohol dehydrogenase and occasionally precipitate a disulfiram-like reaction in patients who consume alcohol while being treated.  Symptoms may include abdominal cramps, nausea, vomiting, headache, flushing, rash, weakness, diarrhea, abdominal pain, dizziness, sweating, and hypotension.  Patients should be instructed to avoid alcohol-containing products during nitroimidazole therapy and for at least 48 hours (fexinidazole, secnidazole) to 72 hours (metronidazole, tinidazole) after the last dose.  Therapy with nitroimidazoles should be administered cautiously in patients who might be prone to acute alcohol intake.  An alternative therapy may be appropriate.', '2', '"Product Information. Flagyl (metronidazole)." Searle  (2002):|"Product Information. Tindamax (tinidazole)." Presutti Laboratories Inc  (2004):|"Product Information. Solosec (secnidazole)." Symbiomix Therapeutics  (2017):|"Product Information. Fexinidazole (fexinidazole)." sanofi-aventis  (2021):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27949, 25437, 'Tinidazole', 'Alcoholism', 'Nitroimidazoles (e.g., metronidazole, tinidazole, fexinidazole, secnidazole) may inhibit alcohol dehydrogenase and occasionally precipitate a disulfiram-like reaction in patients who consume alcohol while being treated.  Symptoms may include abdominal cramps, nausea, vomiting, headache, flushing, rash, weakness, diarrhea, abdominal pain, dizziness, sweating, and hypotension.  Patients should be instructed to avoid alcohol-containing products during nitroimidazole therapy and for at least 48 hours (fexinidazole, secnidazole) to 72 hours (metronidazole, tinidazole) after the last dose.  Therapy with nitroimidazoles should be administered cautiously in patients who might be prone to acute alcohol intake.  An alternative therapy may be appropriate.', '2', '"Product Information. Flagyl (metronidazole)." Searle  (2002):|"Product Information. Tindamax (tinidazole)." Presutti Laboratories Inc  (2004):|"Product Information. Solosec (secnidazole)." Symbiomix Therapeutics  (2017):|"Product Information. Fexinidazole (fexinidazole)." sanofi-aventis  (2021):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27950, 4704, 'Tinidazole', 'Diseases requiring hemodialysis', 'The clearance of tinidazole is significantly increased during hemodialysis; the half-life is reduced from 12 hours to 4.9 hours.  During a 6-hour hemodialysis session, approximately 43% of the amount present in the body is eliminated.  If this drug is administered on the same day as and prior to hemodialysis, an additional dose of tinidazole equivalent to one-half the recommended dose should be administered after the end of the hemodialysis session.', '2', '"Product Information. Tindamax (tinidazole)." Presutti Laboratories Inc  (2004):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27951, 25437, 'Tinidazole', 'Diseases requiring hemodialysis', 'The clearance of tinidazole is significantly increased during hemodialysis; the half-life is reduced from 12 hours to 4.9 hours.  During a 6-hour hemodialysis session, approximately 43% of the amount present in the body is eliminated.  If this drug is administered on the same day as and prior to hemodialysis, an additional dose of tinidazole equivalent to one-half the recommended dose should be administered after the end of the hemodialysis session.', '2', '"Product Information. Tindamax (tinidazole)." Presutti Laboratories Inc  (2004):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27952, 4704, 'Tinidazole', 'Liver Diseases', 'There are no data on the pharmacokinetics of tinidazole in patients with liver dysfunction.  Reduced metabolic elimination of metronidazole (a chemically-related nitroimidazole) has been reported in patients with liver dysfunction.  Usual recommended doses of tinidazole should be administered cautiously in patients with liver dysfunction.', '2', '"Product Information. Tindamax (tinidazole)." Presutti Laboratories Inc  (2004):', 'DDInter', 0);
