INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28426, 4982, 'Tofacitinib', 'Hyperlipidemias', 'Treatment with tofacitinib was associated with dose-dependent increases in lipid parameters (including total cholesterol, low-density lipoprotein [LDL] cholesterol, high-density lipoprotein [HDL] cholesterol); maximum effects generally occurred within 6 weeks.  Lipid parameters should be assessed about 4 to 8 weeks after initiation of therapy.  Patients should be managed according to clinical guidelines for the management of hyperlipidemia.', '2', '"Product Information. Xeljanz (tofacitinib)." Pfizer U.S. Pharmaceuticals Group  (2021):', 'DDInter', 0);
