INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28543, 3015, 'Tolterodine', 'Liver Diseases', 'Tolterodine undergoes biotransformation in the liver of extensive metabolizers to an active metabolite.  Poor metabolizers have a greater concentration of active parent and less active metabolite, however the net activity of tolterodine is expected to be the same.  The pharmacokinetic disposition of tolterodine can be significantly altered in patients with hepatic dysfunction.  Therapy with tolterodine should be administered cautiously in patients with severe hepatic impairment and dosage should be reduced to no greater than 1 mg twice daily.', '2', '"Product Information. Detrol (tolterodine)." Pharmacia and Upjohn  (2001):', 'DDInter', 0);
