INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28657, 3025, 'Topiramate', 'Liver Failure', 'The use of carbonic anhydrase inhibitors is contraindicated in patients with marked liver disease or cirrhosis.  Carbonic anhydrase inhibitors increase the risk of developing hepatic encephalopathy in these patients.  Extreme caution should be exercised if carbonic anhydrase inhibitors are administered in patients with mild to moderate liver disease as the clearance of the drug can be decreased.  A dose reduction might be needed and monitoring of the liver function is recommended.', '3', 'Maren TH "Acetazolamide and advanced liver disease ." Am J Ophthalmol 102 (1986):  672-3|Margo CE "Acetazolamide and advanced liver disease." Am J Ophthalmol 101 (1986):  611-2|"Product Information. Diamox (acetazolamide)." Lederle Laboratories  (2001):|"Product Information. Topamax (topiramate)." Ortho McNeil Pharmaceutical  (2001):|"Product Information. Zonegran (zonisamide)." Elan Pharmaceuticals  (2001):|"Product Information. Neptazane (methazolamide)." Wyeth-Ayerst Laboratories  (2006):', 'DDInter', 0);
