INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28767, 3025, 'Topiramate', 'Diseases requiring hemodialysis', 'Topiramate is cleared by hemodialysis at a rate that is 4 to 6 times greater than that in a normal individual.  A prolonged period of dialysis may cause plasma topiramate level to fall below that required to maintain an anti-seizure effect, thus a supplemental dose may be required in patients undergoing hemodialysis.  The actual adjustment should take into account the duration of dialysis, the clearance rate of the dialysis system being used, and the effective renal clearance of topiramate in the patient being dialyzed.', '2', '"Product Information. Topamax (topiramate)." Ortho McNeil Pharmaceutical  (2001):', 'DDInter', 0);
